}
function getArchiveData()
{
    var devList = [];
    startDate = new Date($( "#startDate" ).val()).getTime();
    stopDate = new Date($( "#stopDate" ).val()).getTime();
    var maxPtsToPlot = Number($( "#maxPtsToPlot" ).val());
    maxPtsToPlot
    var traceCount = 0;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0) 
        {
            devList[traceCount] = {$and : [{name : devInfo[itrace].name}, {timeStamp: { "$gte": startDate, "$lte": stopDate }}] } ;
            ++traceCount;
        }
    }
    if (traceCount < 1) return;
    $('#plotSetupTable').hide();
    $('#timePlotCard').hide();
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        $('#csv'+ itrace.toString()).removeAttr("href");
        $('#csv'+ itrace.toString()).css('color', 'black');
        $('#csv'+ itrace.toString()).css('text-decoration', 'none');
        $('#csv'+ itrace.toString()).css('font-weight', 'bold');
   }

    var queryFilter = { $or : devList};
    messageDialog('Alert', 'Wait!', 'Requesting data ...');

    sendWebSocketMessageObject(
        {
            topic       : 'getArchiveValues',
            userID      : userID,
            queryFilter : queryFilter,
            maxPtsToPlot: maxPtsToPlot
        });
}
function loadArchiveValues(data)
{
    $( "#messageDialog" ).dialog('close');
    $('#plotSetupTable').show();
    var plotTraces = [];
    var devices = data.devices;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0)
        {
            var modeType = 'lines';
            if (devInfo[itrace].pts) modeType = 'markers';
            var trace =  
            {
                x: [],
                y: [],
                name: devInfo[itrace].axis + ' ' + devInfo[itrace].name + '.' + devInfo[itrace].prop + '.' + devInfo[itrace].attr + ' (' + getAttrObject(devInfo[itrace].device, itrace).unit + ')',
                yaxis: devInfo[itrace].axis,
                type: 'scatter',
                mode: modeType
            }
            try 
            {
                var device = devices.find(device => device.name === devInfo[itrace].name);
                for (var ipt = 0; ipt < device.records.length; ++ipt)
                {
                    var attr = getAttrObject(device.records[ipt], itrace);

                    trace.x[ipt] = (device.records[ipt].timeStamp - startDate) / 3600000;
                    trace.y[ipt] = attr.value;
                }
                plotTraces.push(trace);
                makeCsvFile(device, itrace);
            }
            catch(error) 
            {
                console.error(error);
                acknowledgeDialog('Error', 'No Data found', 'Device: ' + devInfo[itrace].name);
                return;
            }
        }
    }
    var plotLayout =
    {
        showlegend: true,
        legend: {x: 0,y: 1.35},
        xaxis: {title: 'Hours  since '  + new Date(startDate).toLocaleString()},
        yaxis: {title: 'y1'},
        yaxis2: {title: 'y2',overlaying: 'y',side: 'right'}
    };
    $('#timePlotCard').show();
    Plotly.newPlot('timePlot', plotTraces, plotLayout);
}
makeCsvFile = function (device, itrace)
{
    var dataString = '';
    var startT = device.records[0].timeStamp;

    dataString = dataString + 'Device,' + devInfo[itrace].name + ',' + devInfo[itrace].prop + ',' + devInfo[itrace].attr  +'\n';
    dataString = dataString + 'StartDate,' + new Date(startT).toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}) + '\n';
    dataString = dataString + 'StartDate (mS),' + startT.toString() + '\n';
    dataString = dataString + 'Time (sec), Value (' + getAttrObject(device.records[0], itrace).unit + ')\n';
    for (var ipt = 0; ipt < device.records.length; ++ipt)
    {
        var attr = getAttrObject(device.records[ipt], itrace);

        var x = (device.records[ipt].timeStamp - startT) / 1000;
        var y = attr.value;
        dataString = dataString + x.toString() + ',' + y.toString() + '\n';
    }
    var data = new Blob([dataString], {type: 'text/plain'});

    // If we are replacing a previously generated file we need to
    // manually revoke the object URL to avoid memory leaks.
    if (csvFile[itrace] !== null) {
      window.URL.revokeObjectURL(csvFile[itrace]);
    }

    csvFile[itrace] = window.URL.createObjectURL(data);
    // returns a URL you can use as a href
    $('#csv'+ itrace.toString()).css('color', 'blue');
    $('#csv'+ itrace.toString()).css('text-decoration', 'underline');
    $('#csv'+ itrace.toString()).css('font-weight', 'bold');
    $('#csv'+ itrace.toString()).attr("href", csvFile[itrace]);
    $('#csv'+ itrace.toString()).attr("download", devInfo[itrace].name + '-' + devInfo[itrace].prop + '-' + devInfo[itrace].attr + '.csv');

}

