var projectionFilter =  {projection:{_id:0}};
var newMsg = 
{
    topic           : 'getArchiveValues',
    userID          : msg.payload.userID,
    payload         : [msg.payload.queryFilter, projectionFilter],
    queryFilter     : msg.payload.queryFilter,
    maxPtsToPlot    : msg.payload.maxPtsToPlot
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}

function sortByKey(arr, key) 
{
    return arr.sort( function(a, b) 
        {
            var x = a[key]; 
            var y = b[key];
            return ((x < y) ? -1 : ((x > y) ? 1 : 0));
        } );
}

var numRecords = objectLength(msg.payload);
if (numRecords < 1)
{
    return {
        topic:'getArchiveValues',
        payload:{
            topic           : 'getArchiveValues',
            devices         : null,
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) records[ii] = msg.payload[ii]; 
sortByKey(records, 'name');
var deviceNames = [];
deviceNames[0] = records[0].name; 
var nameCount = 1;
for (var ii = 0; ii < numRecords; ++ii)
{
    if (records[ii].name != deviceNames[nameCount - 1])
    {
        deviceNames[nameCount] = records[ii].name;
        ++nameCount
    }
}
var deviceData = [];
var deviceDataParse = [];
for (var iname = 0; iname < nameCount; ++iname) 
{
    deviceData[iname] = [];
    deviceDataParse[iname] = [];
}
nameCount = 1;
var devCount = 0;
for (var ii = 0; ii < numRecords; ++ii)
{
    if (records[ii].name != deviceNames[nameCount - 1])
    {
        ++nameCount;
        devCount = 0;
    }
    else
    {
        deviceData[nameCount - 1][devCount] = records[ii];
        ++devCount
    }
}
for (var iname = 0; iname < nameCount; ++iname)
{
    var maxPts = msg.maxPtsToPlot;
    sortByKey(deviceData[iname], 'timeStamp');
    var nstep = deviceData[iname].length / msg.maxPtsToPlot;
    if (nstep < 1.0) 
    {
        nstep = 1.0;
        maxPts = deviceData[iname].length;
    }
    var icnt = 0;
    while(icnt < maxPts)
    {
        deviceDataParse[iname][icnt] = deviceData[iname][Math.round(icnt * nstep)];
        ++icnt
    }
    deviceData[iname] = 
    {
        name    : deviceDataParse[iname][0].name, 
        records : deviceDataParse[iname]
    };
}
return {
    topic:'getArchiveValues',
    payload:{
        topic           : 'getArchiveValues',
        devices         : deviceData,
        userID          : msg.userID,
    }
};

