        { attr : 'temperature', type : 'scalar',    value : 0,  unit : 'C',     alarm : {value : 0, limits: {lolo : -200,    low : -101,  high : 120,      hihi : 150   }, notify:{name:'', number:'', time:0} } },
        { attr : 'linkQuality', type : 'scalar',    value : 0,  unit : '/70',   alarm : {value : 0, limits: {lolo :  30,     low : 40,    high : 71,       hihi : 80    }, notify:{name:'', number:'', time:0} } },
        { attr : 'signalLevel', type : 'scalar',    value : 0,  unit : 'dBm',   alarm : {value : 0, limits: {lolo : -70,     low : -60,   high : 0,        hihi : 30    }, notify:{name:'', number:'', time:0} } }
    ],
