    [ 
        { attr : 'duty-factor-samples',   type : 'scalar',    value : 5,    unit : 'unit',    alarm : {value : 0, limits: {lolo : 1,    low : 2,    high : 100,    hihi : 200}, notify:{name:'', number:'', time:0} } },
        { attr : 'temp-samples',          type : 'scalar',    value : 5,    unit : 'unit',    alarm : {value : 0, limits: {lolo : 1,    low : 2,    high : 100,    hihi : 200}, notify:{name:'', number:'', time:0} } },
        { attr : 'ramp-down-time',        type : 'scalar',    value : 1.0,  unit : 'hours',   alarm : {value : 0, limits: {lolo : 0.1,  low : 0.2,  high : 50,     hihi : 100}, notify:{name:'', number:'', time:0} } },
        { attr : 'ramp-up-time',          type : 'scalar',    value : 1.0,  unit : 'hours',   alarm : {value : 0, limits: {lolo : 0.1,  low : 0.2,  high : 50,     hihi : 100}, notify:{name:'', number:'', time:0} } },
        { attr : 'reg-temp',              type : 'scalar',    value : 50.0, unit : 'C',       alarm : {value : 0, limits: {lolo : -10,  low : -20,  high : 120,    hihi : 150}, notify:{name:'', number:'', time:0} } },
        { attr : 'reg-window',            type : 'scalar',    value : 1.0,  unit : 'C',       alarm : {value : 0, limits: {lolo : 0.01, low : 0.2,  high : 10,     hihi : 20 }, notify:{name:'', number:'', time:0} } },
        { attr : 'state',                 type : 'scalar',    value : 0,    unit : 'unit',    alarm : {value : 0, limits: {lolo : -1,   low : -0.5, high : 10,     hihi : 11 }, notify:{name:'', number:'', time:0} } }
   ]
};
return {topic:'bakeout/dma02/setting', payload:device};

if (msg.payload.notify.name.length < 1) return null;
if (msg.payload.notify.number.length < 1) return null;
var type = '';
if (msg.payload.alarm == 1) type = 'LOW';
if (msg.payload.alarm == 2) type = 'HIGH';
if (msg.payload.alarm == 3) type = 'LOLO';
if (msg.payload.alarm == 4) type = 'HIHI';
var message = 'Alarm-' + type + ': ' + msg.payload.device + '-'  + msg.payload.prop + '-'  + msg.payload.attr;
message = message + ' is at ' + msg.payload.value.toString() + ' ' + msg.payload.unit
return {topic:'sms',to:msg.payload.notify.number, payload:message, from:'46000000000'};

msg.userData = msg.payload;  
msg.payload = {  
    username: msg.userData.username
};
return msg;

var databaseInfo = 
{
    "username": "admin",
    "password": "secret"
}
msg.payload = databaseInfo;
return msg;

if (msg.payload.token) {  
   msg.token = msg.payload.token;
   node.send(msg);
} else {
   msg.statusCode = 403;
   node.send([null, msg]);
}
return msg;

if (msg.payload.payload.loginPassword != global.get('settingsPassword'))
{
    return [{topic:'getJwtToken', payload:{topic:'getJwtToken', userID:msg.payload.userID, token: null}}, null];
}
return [null, {topic:'getJwtToken',payload:msg.payload.userID, userID : msg.payload.userID}];

if (Number(msg.token) == msg.userID)
{
    return [msg, {topic : 'acknowledge', payload:{topic:'settingAcknowledge', payload: 'Success!', userID : msg.userID}}]
}
return [null, {topic : 'acknowledge', payload:{topic:'settingAcknowledge', payload: 'Bad token', userID : msg.userID}}];

