template:
var numTraces = 1;
var deviceSelect = [];
var propSelect=[];
var attrSelect=[];
var valSelect=[];
var devInfo = [];

var graph3d = null;
var itimeSlice = 0;
var timeMax = 32;
var dataArray = [];
var timeStamp = [];
var deltaT = 0;
var freeze = false;
var sliceViewActive = false;
var birdsEyeViewActive = false;
var topViewActive = false;
var birdsEyeTimeMax = 32;
var plotStarted = false;

var threedPlotOptions =
{
    width: '850px',
    height: '600px',
    style: 'surface',
    showPerspective: false,
    showGrid: true,
    showShadow: false,
    keepAspectRatio: false,
    verticalRatio: 0.6,
    showZAxis: true,
    xLabel: 'x',
    yLabel: 'Time',
    tooltip: true,
    axisColor: '#ffffff',
};

useWebSockets(true);
function onDocumentReady()
{
    $("#freezeButton").text('Running');
    $("#plotslices").val(timeMax.toString());
    $("#plotslices").change(function()
    {
        timeMax = Number($("#plotslices").val());
        if (timeMax < 2)
        {
            alert('Number of plot slices must be greater than 1');
            timeMax = 2;
        }
        birdsEyeTimeMax = timeMax;
        $("#plotslices").val(timeMax.toString());
        dataArray = [];
        itimeSlice = 0;
        plotStarted = false;
    });
    sliceViewActive = true;
    birdsEyeViewActive = false;
    topViewActive = false;

    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        deviceSelect[itrace] = document.getElementById('deviceSelect' + itrace.toString());
        propSelect[itrace] = document.getElementById('propSelect' + itrace.toString());
        attrSelect[itrace] = document.getElementById('attrSelect' + itrace.toString());
        devInfo[itrace] = {name : '', prop : '', attr : '',device : {}};
    }
    setInterval(getDeviceValues, 1000);
}
function onWebSocketOpen()
{
    sendWebSocketMessageObject({topic : 'getDeviceList',payload : {}, 'userID' : userID,});
}
function onWebSocketClose()
{

}
function onWebSocketMessage(msg)
{
    if (msg.userID == userID)
    {
        switch(msg.topic)
        {
            case 'getDeviceList':
                loadDeviceList(msg.payload);
                break;
            case 'getAttrList':
                loadAttrList(msg);
                break;
            case 'getDeviceValues':
                loadDeviceValues(msg);
                break;
            default:
            // code block
        }
    }
}

function loadDeviceList(deviceList)
{
    deviceList.sort();
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        for (var ii = 0; ii < deviceList.length; ++ii)
        {
            var opt = document.createElement('option');
            opt.value = deviceList[ii];
            opt.innerHTML = deviceList[ii];
            deviceSelect[itrace].appendChild(opt);
        }
        var readOpt = document.createElement('option');
        readOpt.value = 'readings';
        readOpt.innerHTML = 'readings';
        propSelect[itrace].appendChild(readOpt);
        var setOpt = document.createElement('option');
        setOpt.value = 'settings';
        setOpt.innerHTML = 'settings';
        propSelect[itrace].appendChild(setOpt);
    }
}
function deviceSelected(itrace)
{
    propSelect[itrace].value = 'notSelected';
    clearAttrOptionList(itrace);
}
function propSelected(itrace)
{
    if (propSelect[itrace].value == 'notSelected') return;
    if(deviceSelect[itrace].value == 'notSelected')
    {
        propSelect[itrace].value = 'notSelected';
        return;
    }
    clearAttrOptionList(itrace);
    sendWebSocketMessageObject(
        {
            topic       : 'getAttrList',
            userID      : userID,
            trace       : itrace,
            name        : deviceSelect[itrace].value,
            prop        : propSelect[itrace].value
        });
}
function loadAttrList(data)
{
    data.attrList.sort();
    for (var ii = 0; ii < data.attrList.length; ++ii)
    {
        var opt = document.createElement('option');
        opt.value = data.attrList[ii];
        opt.innerHTML = data.attrList[ii];
        attrSelect[data.trace].appendChild(opt);
    }
}
function attrSelected(itrace)
{
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};
    if (attrSelect[itrace].value == 'notSelected') 
    {
        $('#valueSelect' + itrace.toString()).html('');
        $('#unitSelect' + itrace.toString()).html('');
        return;
    }
    devInfo[itrace].name = deviceSelect[itrace].value;
    devInfo[itrace].prop = propSelect[itrace].value;
    devInfo[itrace].attr = attrSelect[itrace].value;


}
function clearAttrOptionList(itrace)
{
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};

    while (attrSelect[itrace].firstChild)
    {
        attrSelect[itrace].removeChild(attrSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    attrSelect[itrace].appendChild(opt);
    $('#valueSelect' + itrace.toString()).html('');
    $('#unitSelect' + itrace.toString()).html('');
}
function getDeviceValues()
{
    var devList = [];
    var traceCount = 0;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0) 
        {
            devList[traceCount] = {name : devInfo[itrace].name};
            ++traceCount;
        }
    }
    if (traceCount < 1) return;
    var queryFilter = { $or: devList };
    sendWebSocketMessageObject(
        {
            topic       : 'getDeviceValues',
            userID      : userID,
            queryFilter : queryFilter
        });

}
function loadDeviceValues(data)
{
    var devices = data.devices;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0)
        {
            devInfo[itrace].device = devices.find(device => device.name === devInfo[itrace].name);
            var attr = getAttrObject(devInfo[itrace].device, itrace);
        }
    }
    upDatePlot(attr.value);
}
function upDatePlot(value)
{
    var npts = value[0].length;
    if(!plotStarted)
    {
        dataArray = [];
        for (var ix = 0; ix < npts; ++ix)
        {
            dataArray[ix] =
            {
                'x': value[0][ix],
                'y': 0,
                'z': value[1][ix],
                'style': value[1][ix]
            };
        }
        var container = document.getElementById('spectrogram');

        graph3d = new vis.Graph3d(container, dataArray, threedPlotOptions);
        $("#startTime").text(new Date().toLocaleString());
        $("#stopTime").text(new Date().toLocaleString());
        graph3d.on('cameraPositionChange', onCameraPositionChange);
        if (sliceViewActive) sliceView();
        if (birdsEyeViewActive) birdsEyeView();
        if (topViewActive) topView();
        plotStarted = true;
    }
    else
    {
        if (freeze) return;
        if (itimeSlice < timeMax)
        {
            timeStamp[itimeSlice] = new Date().getTime();
            deltaT = (timeStamp[itimeSlice] - timeStamp[0]) / 1000;
            for (var ix = 0; ix < npts; ++ix)
            {
                dataArray[itimeSlice * npts + ix] =
                {
                    'x': value[0][ix],
                    'y': deltaT,
                    'z': value[1][ix],
                    'style': value[1][ix]
                };
            }
            ++itimeSlice;
        }
        else
        {
            for (var itime = 0; itime < (timeMax - 1); ++itime)
            {
                timeStamp[itime] = timeStamp[itime + 1];
                deltaT = (timeStamp[itime] - timeStamp[0]) / 1000;
                for (var ix = 0; ix < npts; ++ix)
                {
                    dataArray[itime * npts + ix] =
                    {
                        'x': dataArray[(itime + 1) * npts + ix].x,
                        'y': deltaT,
                        'z': dataArray[(itime + 1) * npts + ix].z,
                        'style': dataArray[(itime + 1) * npts + ix].style
                    };
                }
            }
            timeStamp[timeMax - 1] = new Date().getTime();
            deltaT = (timeStamp[timeMax - 1] - timeStamp[0]) / 1000;
            for (var ix = 0; ix < npts; ++ix)
            {
                dataArray[(timeMax - 1) * npts + ix] =
                {
                    'x': value[0][ix],
                    'y': deltaT,
                    'z': value[1][ix],
                    'style': value[1][ix]
                };
            }
        }
        $("#startTime").text(new Date(timeStamp[0]).toLocaleString());
        $("#stopTime").text(new Date(timeStamp[itimeSlice - 1]).toLocaleString());
        graph3d.setData(dataArray);
    }
}
function getAttrObject(device, itrace)
{
    var props = device[devInfo[itrace].prop];
    var attr = props.find(attr => attr.attr === devInfo[itrace].attr);
    return attr;
}
function freezeRun()
{
    freeze = !freeze;
    if (freeze)
    {
        $("#freezeButton").text('Frozen');
    }
    else
    {
        $("#freezeButton").text('Running');
    }
}

function birdsEyeView()
{
    var horzAngle = 315.0 * 3.1415927 / 180.0;
    var vertAngle = 45.0 * 3.1415927 / 180.0;
    graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
    if (sliceViewActive)
    {
        timeMax =  birdsEyeTimeMax;
        $("#plotslices").val(timeMax.toString());
    }
    sliceViewActive = false;
    birdsEyeViewActive = true;
    topViewActive = false;
}
function sliceView()
{
    var horzAngle = 0.0 * 3.1415927 / 180.0;
    var vertAngle = 0.0 * 3.1415927 / 180.0;
    graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 1.1});
    timeMax = 2;
    $("#plotslices").val(timeMax.toString());
    itimeSlice = 0;
    plotStarted = false;
    sliceViewActive = true;
    birdsEyeViewActive = false;
    topViewActive = false;
}
function topView()
{
    var horzAngle = 0 * 3.1415927 / 180.0;
    var vertAngle = 90 * 3.1415927 / 180.0;
    graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
    if (sliceViewActive)
    {
        timeMax =  birdsEyeTimeMax;
        $("#plotslices").val(timeMax.toString());
    }
    sliceViewActive = false;
    birdsEyeViewActive = false;
    topViewActive = true;
}
function onCameraPositionChange(event)
{
    if (sliceViewActive) sliceView();
    if (birdsEyeViewActive) birdsEyeView();
    if (topViewActive) topView();
}

func:
var newMsg = 
{
    topic           : 'getDeviceList',
    userID          : msg.payload.userID,
    payload         : [{},{projection:{name:1, _id:0}}]
};
return newMsg;

var projectionFilter =  {projection:{readings:1,_id:0}};
if (msg.payload.prop == 'settings') projectionFilter =  {projection:{settings:1,_id:0}};
var newMsg = 
{
    topic           : 'getAttrList',
    userID          : msg.payload.userID,
    payload         : [{name:msg.payload.name}, projectionFilter],
    prop            : msg.payload.prop,
    name            : msg.payload.name,
    trace           : msg.payload.trace
};
return newMsg;

var projectionFilter =  {projection:{_id:0}};
var newMsg = 
{
    topic           : 'getDeviceValues',
    userID          : msg.payload.userID,
    payload         : [msg.payload.queryFilter, projectionFilter],
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}
var numDevices = objectLength(msg.payload);
var deviceList = [];

for (var idevice = 0; idevice < numDevices; ++idevice)
{
    deviceList[idevice] = msg.payload[idevice].name;
}
return {
    topic:'getDeviceList',
    payload:{
        topic           : 'getDeviceList',
        payload         : deviceList,
        userID          : msg.userID,
    }
};

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}
var numDevices = objectLength(msg.payload);
if (numDevices < 1) return null;
var attrListBig = msg.payload[0][msg.prop];
var attrList = [];
var attrCount = 0;
for (var ii = 0; ii < attrListBig.length; ++ii)
{
    if (attrListBig[ii].type == 'vector')
    {
        attrList[attrCount] = attrListBig[ii].attr;
        ++attrCount
    }
}
if (attrCount < 1) return null;
return {
    topic:'getAttrList',
    payload:{
        topic           : 'getAttrList',
        userID          : msg.userID,
        prop            : msg.prop,
        name            : msg.name,
        trace           : msg.trace,
        attrList        : attrList

    }
};

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}
var numDevices = objectLength(msg.payload);
if (numDevices < 1) return null;
var devices = [];
for (var idev = 0; idev < numDevices; ++idev) devices[idev] = msg.payload[idev];
return {
    topic:'getDeviceValues',
    payload:{
        topic           : 'getDeviceValues',
        userID          : msg.userID,
        devices         : devices,

    }
};

var projectionFilter =  {projection:{_id:0}};
var newMsg = 
{
    topic           : 'getArchiveValues',
    userID          : msg.payload.userID,
    payload         : [msg.payload.queryFilter, projectionFilter],
    queryFilter     : msg.payload.queryFilter,
    maxPtsToPlot    : msg.payload.maxPtsToPlot
};
return newMsg;

function objectLength( object ) {
    var length = 0;
    for( var key in object ) {
        if( object.hasOwnProperty(key) ) {
            ++length;
        }
    }
    return length;
}

function sortByKey(arr, key) 
{
    return arr.sort( function(a, b) 
        {
            var x = a[key]; 
            var y = b[key];
            return ((x < y) ? -1 : ((x > y) ? 1 : 0));
        } );
}

var numRecords = objectLength(msg.payload);
if (numRecords < 1)
{
    return {
        topic:'getArchiveValues',
        payload:{
            topic           : 'getArchiveValues',
            devices         : null,
            userID          : msg.userID,
        }
    };
}
var records = [];
for (var ii = 0; ii < numRecords; ++ii) records[ii] = msg.payload[ii]; 
sortByKey(records, 'name');
var deviceNames = [];
deviceNames[0] = records[0].name; 
var nameCount = 1;
for (var ii = 0; ii < numRecords; ++ii)
{
    if (records[ii].name != deviceNames[nameCount - 1])
    {
        deviceNames[nameCount] = records[ii].name;
        ++nameCount
    }
}
var deviceData = [];
var deviceDataParse = [];
for (var iname = 0; iname < nameCount; ++iname) 
{
    deviceData[iname] = [];
    deviceDataParse[iname] = [];
}
nameCount = 1;
var devCount = 0;
for (var ii = 0; ii < numRecords; ++ii)
{
    if (records[ii].name != deviceNames[nameCount - 1])
    {
        ++nameCount;
        devCount = 0;
    }
    else
    {
        deviceData[nameCount - 1][devCount] = records[ii];
        ++devCount
    }
}
for (var iname = 0; iname < nameCount; ++iname)
{
    var maxPts = msg.maxPtsToPlot;
    sortByKey(deviceData[iname], 'timeStamp');
    var nstep = deviceData[iname].length / msg.maxPtsToPlot;
    if (nstep < 1.0) 
    {
        nstep = 1.0;
        maxPts = deviceData[iname].length;
    }
    var icnt = 0;
    while(icnt < maxPts)
    {
        deviceDataParse[iname][icnt] = deviceData[iname][Math.round(icnt * nstep)];
        ++icnt
    }
    deviceData[iname] = 
    {
        name    : deviceDataParse[iname][0].name, 
        records : deviceDataParse[iname]
    };
}
return {
    topic:'getArchiveValues',
    payload:{
        topic           : 'getArchiveValues',
        devices         : deviceData,
        userID          : msg.userID,
    }
};

template:
var numTraces = 1;
var deviceSelect = [];
var propSelect=[];
var attrSelect=[];
var valSelect=[];
var devInfo = [];

var startDate = 0;
var stopDate = 0;
var csvFile = [];

useWebSockets(true);
function onDocumentReady()
{
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        deviceSelect[itrace] = document.getElementById('deviceSelect' + itrace.toString());
        propSelect[itrace] = document.getElementById('propSelect' + itrace.toString());
        attrSelect[itrace] = document.getElementById('attrSelect' + itrace.toString());
        valSelect[itrace] = document.getElementById('valSelect' + itrace.toString());
        devInfo[itrace] = {name : '', prop : '', attr : '',device : {}};

        $('#csv'+ itrace.toString()).removeAttr("href");
        $('#csv'+ itrace.toString()).css('color', 'black');
        $('#csv'+ itrace.toString()).css('text-decoration', 'none');
        $('#csv'+ itrace.toString()).css('font-weight', 'bold');
        csvFile[itrace] = null;

    }
    setInterval(getDeviceValues, 1000);
    var now = new Date();
    console.log(now.toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', ''));
    var then = new Date(now.getTime() - 3600 * 24 * 1000);
    $( "#startDate" ).val(then.toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', ''));
    $( "#stopDate" ).val(now.toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}).replace(',', ''));
    $( function()
    {
        $('#startDate').datetimepicker();
    } );
    $( function()
    {
        $('#stopDate').datetimepicker();
    } );
    $('#plotViewButtons').hide();
}
function onWebSocketOpen()
{
    sendWebSocketMessageObject({topic : 'getDeviceList',payload : {}, 'userID' : userID,});
}
function onWebSocketClose()
{

}
function onWebSocketMessage(msg)
{
    if (msg.userID == userID)
    {
        switch(msg.topic)
        {
            case 'getDeviceList':
                loadDeviceList(msg.payload);
                break;
            case 'getAttrList':
                loadAttrList(msg);
                break;
            case 'getDeviceValues':
                loadDeviceValues(msg);
                break;
            case 'getArchiveValues':
                loadArchiveValues(msg);
                break;
            default:
            // code block
        }
    }
}
function loadDeviceList(deviceList)
{
    deviceList.sort();
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        for (var ii = 0; ii < deviceList.length; ++ii)
        {
            var opt = document.createElement('option');
            opt.value = deviceList[ii];
            opt.innerHTML = deviceList[ii];
            deviceSelect[itrace].appendChild(opt);
        }
        var readOpt = document.createElement('option');
        readOpt.value = 'readings';
        readOpt.innerHTML = 'readings';
        propSelect[itrace].appendChild(readOpt);
        var setOpt = document.createElement('option');
        setOpt.value = 'settings';
        setOpt.innerHTML = 'settings';
        propSelect[itrace].appendChild(setOpt);
    }
}
function deviceSelected(itrace)
{
    propSelect[itrace].value = 'notSelected';
    clearAttrOptionList(itrace);
}
function propSelected(itrace)
{
    if (propSelect[itrace].value == 'notSelected') return;
    if(deviceSelect[itrace].value == 'notSelected')
    {
        propSelect[itrace].value = 'notSelected';
        return;
    }
    clearAttrOptionList(itrace);
    sendWebSocketMessageObject(
        {
            topic       : 'getAttrList',
            userID      : userID,
            trace       : itrace,
            name        : deviceSelect[itrace].value,
            prop        : propSelect[itrace].value
        });
}
function loadAttrList(data)
{
    data.attrList.sort();
    for (var ii = 0; ii < data.attrList.length; ++ii)
    {
        var opt = document.createElement('option');
        opt.value = data.attrList[ii];
        opt.innerHTML = data.attrList[ii];
        attrSelect[data.trace].appendChild(opt);
    }
}
function attrSelected(itrace)
{
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};
    devInfo[itrace].name = deviceSelect[itrace].value;
    devInfo[itrace].prop = propSelect[itrace].value;
    devInfo[itrace].attr = attrSelect[itrace].value;
    $('#csv'+ itrace.toString()).removeAttr("href");
    $('#csv'+ itrace.toString()).css('color', 'black');
    $('#csv'+ itrace.toString()).css('text-decoration', 'none');
    $('#csv'+ itrace.toString()).css('font-weight', 'bold');


}
function clearAttrOptionList(itrace)
{
    devInfo[itrace].name = '';
    devInfo[itrace].prop = '';
    devInfo[itrace].attr = '';
    devInfo[itrace].device = {};

    while (attrSelect[itrace].firstChild)
    {
        attrSelect[itrace].removeChild(attrSelect[itrace].firstChild);
    }
    var opt = document.createElement('option');
    opt.value = 'notSelected';
    opt.innerHTML = '';
    attrSelect[itrace].appendChild(opt);
    $('#csv'+ itrace.toString()).removeAttr("href");
    $('#csv'+ itrace.toString()).css('color', 'black');
    $('#csv'+ itrace.toString()).css('text-decoration', 'none');
    $('#csv'+ itrace.toString()).css('font-weight', 'bold');
}
function getDeviceValues()
{
    var devList = [];
    var traceCount = 0;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0) 
        {
            devList[traceCount] = {name : devInfo[itrace].name};
            ++traceCount;
        }
    }
    if (traceCount < 1) return;
    var queryFilter = { $or: devList };
    sendWebSocketMessageObject(
        {
            topic       : 'getDeviceValues',
            userID      : userID,
            queryFilter : queryFilter
        });

}
function loadDeviceValues(data)
{
    var devices = data.devices;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0)
        {
            devInfo[itrace].device = devices.find(device => device.name === devInfo[itrace].name);
            var attr = getAttrObject(devInfo[itrace].device, itrace);
        }
    }
}
function getAttrObject(device, itrace)
{
    var props = device[devInfo[itrace].prop];
    var attr = props.find(attr => attr.attr === devInfo[itrace].attr);
    return attr;
}
function getArchiveData()
{
    var devList = [];
    startDate = new Date($( "#startDate" ).val()).getTime();
    stopDate = new Date($( "#stopDate" ).val()).getTime();
    var maxPtsToPlot = Number($( "#maxPtsToPlot" ).val());
    maxPtsToPlot
    var traceCount = 0;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0) 
        {
            devList[traceCount] = {$and : [{name : devInfo[itrace].name}, {timeStamp: { "$gte": startDate, "$lte": stopDate }}] } ;
            ++traceCount;
        }
    }
    if (traceCount < 1) return;
    $('#plotSetupTable').hide();
    $('#timePlotCard').hide();
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        $('#csv'+ itrace.toString()).removeAttr("href");
        $('#csv'+ itrace.toString()).css('color', 'black');
        $('#csv'+ itrace.toString()).css('text-decoration', 'none');
        $('#csv'+ itrace.toString()).css('font-weight', 'bold');
   }

    var queryFilter = { $or : devList};
    messageDialog('Alert', 'Wait!', 'Requesting data ...');

    sendWebSocketMessageObject(
        {
            topic       : 'getArchiveValues',
            userID      : userID,
            queryFilter : queryFilter,
            maxPtsToPlot: maxPtsToPlot
        });
}
function loadArchiveValues(data)
{
    $( "#messageDialog" ).dialog('close');
    $('#plotSetupTable').show();
     var devices = data.devices;
    for (var itrace = 0; itrace < numTraces; ++itrace)
    {
        if (devInfo[itrace].name.length > 0)
        {
            try 
            {
                var device = devices.find(device => device.name === devInfo[itrace].name);
                plotArchive(device, itrace);
                makeCsvFile(device, itrace);
            }
            catch(error) 
            {
                console.error(error);
                acknowledgeDialog('Error', 'No Data found', 'Device: ' + devInfo[itrace].name);
                $('#plotSetupTable').show();
                return;
            }
        }
    }
}
function plotArchive(device, itrace)
{
    $('#plotSetupTable').show();
    $('#plotViewButtons').show();
    $('#timePlot').show();
    var npts = device.records.length;
    var oodataArray = [];
    var nxMax = getAttrObject(device.records[0], itrace).value[0].length;
    for (var ii = 0; ii < npts; ++ii)
    {
        var attr = getAttrObject(device.records[ii], itrace);
        var yvalue = Math.round((device.records[ii].timeStamp - device.records[0].timeStamp)/3600) / 1000;
        for (var ix = 0; ix < nxMax; ++ix)
        {
            var zvalue = attr.value[1][ix];
            oodataArray[ii * nxMax + ix] =
            {
                'x': attr.value[0][ix],
                'y': yvalue,
                'z': zvalue,
                'style': zvalue
            };
        }
    }
    var options = 
    {
        width:  '100%',
        height: '800px',
        style: 'surface',
        showPerspective: false,
        showGrid: true,
        showShadow: false,
        keepAspectRatio: false,
        verticalRatio: 1.0,
        showZAxis: true,
        yCenter: '50%',
        xLabel: 'x',
        yLabel: 'Time (hr)',
        zLabel: 'y',
        tooltip: true,
        axisColor: '#000000'
    };

    // create a graph3d
    var container = document.getElementById('timePlot');

    graph3d = new vis.Graph3d(container, oodataArray, options);
    var horzAngle = 0 * 3.1415927 / 180.0;
    var vertAngle = 90 * 3.1415927 / 180.0;
    graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
}
function changePlotView(horzAngleDeg, vertAngleDeg)
{
    var horzAngle = horzAngleDeg * 3.1415927 / 180.0;
    var vertAngle = vertAngleDeg * 3.1415927 / 180.0;
    graph3d.setCameraPosition({'horizontal': horzAngle, 'vertical': vertAngle, 'distance': 2.0});
}
function makeCsvFile (device, itrace)
{
    var dataString = '';
    var startT = device.records[0].timeStamp;

    dataString = dataString + 'Device,' + devInfo[itrace].name + ',' + devInfo[itrace].prop + ',' + devInfo[itrace].attr  +'\n';
    dataString = dataString + 'StartDate,' + new Date(startT).toLocaleString('en-US',{year: 'numeric', month: '2-digit', day : '2-digit', hour12:false, hour: '2-digit', minute: '2-digit'}) + '\n';
    dataString = dataString + 'StartDate (mS),' + startT.toString() + '\n';
    dataString = dataString + 'Time (sec),  \n 0,x';
    var attr0 = getAttrObject(device.records[0], itrace);
    for (var ipt = 0; ipt < attr0.value[0].length; ++ipt) dataString = dataString + ',' + attr0.value[0][ipt];
    dataString = dataString + '\n';

    for (var ii = 0; ii < device.records.length; ++ii)
    {
        var attr = getAttrObject(device.records[ii], itrace);

        var x = (device.records[ii].timeStamp - startT) / 1000;
        dataString = dataString + x.toString() + ',y';
        for (var ipt = 0; ipt < attr.value[1].length; ++ipt) dataString = dataString + ',' + attr.value[1][ipt];
        dataString = dataString + '\n';
    }
    var data = new Blob([dataString], {type: 'text/plain'});

    // If we are replacing a previously generated file we need to
    // manually revoke the object URL to avoid memory leaks.
    if (csvFile[itrace] !== null) {
      window.URL.revokeObjectURL(csvFile[itrace]);
    }

    csvFile[itrace] = window.URL.createObjectURL(data);
    // returns a URL you can use as a href
    $('#csv'+ itrace.toString()).css('color', 'blue');
    $('#csv'+ itrace.toString()).css('text-decoration', 'underline');
    $('#csv'+ itrace.toString()).css('font-weight', 'bold');
    $('#csv'+ itrace.toString()).attr("href", csvFile[itrace]);
    $('#csv'+ itrace.toString()).attr("download", devInfo[itrace].name + '-' + devInfo[itrace].prop + '-' + devInfo[itrace].attr + '.csv');

}

