                showlegend: false,
                marker:{color: [barConfig.mediumColor, mediumColor]},
                width: [0.1, 1.0],
            };
            var highBar = 
            {
                x: ['', barConfig.label],
                y: [barConfig.hihi - barConfig.high, highValue],
                type: 'bar',
                showlegend: false,
                marker:{color: [barConfig.highColor, highColor]},
                width: [0.1, 1.0],
            };

            var data = [lowBar, mediumBar, highBar];

            var layout = 
                {
                    barmode: 'stack',
                    plot_bgcolor:barConfig.backgroundColor,
